mapper.select:
SELECT id, session_id, user_id, memory_key, last_active_at, created_at, updated_at
FROM chat_sessions
WHERE user_id = #{userId}
  AND session_id = #{sessionId}
LIMIT 1

mapper.update:
UPDATE chat_sessions
SET last_active_at = NOW()
WHERE user_id = #{userId}
  AND session_id = #{sessionId}
